mapper.update:
UPDATE schedule
schedule_title = #{scheduleTitle:VARCHAR}, schedule_contents = #{scheduleContents:VARCHAR}, schedule_start_date = TO_DATE(#{scheduleStartDate},'YYYY-MM-DD HH24:MI'), schedule_end_date = TO_DATE(#{scheduleEndDate},'YYYY-MM-DD HH24:MI'), schedule_color = #{scheduleColor:VARCHAR}
WHERE schedule_no = #{scheduleNo}
