mapper.select:
SELECT *
FROM menu
WHERE code = #{code}
AND	revision_code = #{revision_code}

mapper.update:
UPDATE menu
CATEGORY_CODE = #{category_code}, REVISION_CODE = #{revision_code}, TITLE = #{title : VARCHAR}, CONTENT = #{content : VARCHAR}, IS_OUTER_LINK =#{is_outer_link : VARCHAR}, TARGET = #{target : VARCHAR}, URL =#{url : VARCHAR}, STATUS = #{status : VARCHAR}, MENU_HEAD_CODE = #{menu_head_code : VARCHAR}, IS_META = #{is_meta : VARCHAR}, META_TITLE = #{meta_title : VARCHAR}, META_DESCRIPTION = #{meta_description : VARCHAR}
WHERE CODE = #{code:NUMERIC}
