mapper.select:
select * from popup 


		${field} like concat('%',#{keyword},'%')



			and start_date >= #{start_date} 


			and start_date >= #{start_date} 
			and end_date <= #{end_date}


			and end_date <= #{end_date}




order by code

 select * from coupon


			${field} like concat('%',#{keyword},'%')



				and expiry_start_date >= #{expiry_start_date} 


				and expiry_start_date >= #{expiry_start_date} 
				and expiry_end_date <= #{expiry_end_date}


				and expiry_end_date <= #{expiry_end_date}

			or expiry_type = 'unlimited'


 order by coupon_no
