mapper.select:
SELECT *, (SELECT count(amount) FROM v_product_statistics WHERE order_status_no >= 2) paymentCount, (SELECT COUNT(amount) FROM v_product_statistics WHERE order_status_no = 7) returnCount, (SELECT SUM(amount) FROM v_product_statistics WHERE order_status_no >= 2) paymentSum, (ROW_NUMBER() over(ORDER BY (SELECT COUNT(amount) FROM v_product_statistics WHERE order_status_no >= 2 AND order_status_no != 7) DESC)) AS productRank FROM v_product_statistics
WHERE reg_date between #{search.start_date} and #{search.end_date}
AND category_no = #{search.categoryCode}
ORDER BY productRank ASC
